  // @format
  
  function parseCSV(inData, delimiter) {
    var isStr = highed.isStr,
      isArr = highed.isArray,
      isNum = highed.isNum,
      csv = inData || '',
      result = [],
      options = {
        delimiter: delimiter
      },
      potentialDelimiters = {
        ',': true,
        ';': true,
        '\t': true
      },
      delimiterCounts = {
        ',': 0,
        ';': 0,
        '\t': 0
      };
    //The only thing CSV formats have in common..
    rows = (csv || '').replace(/\r\n/g, '\n').split('\n');
    // If there's no delimiter, look at the first few rows to guess it.
  
    if (!options.delimiter) {
      rows.some(function(row, i) {
        if (i > 10) return true;
  
        var inStr = false,
          c,
          cn,
          cl,
          token = '';
  
        for (var j = 0; j < row.length; j++) {
          c = row[j];
          cn = row[j + 1];
          cl = row[j - 1];
  
          if (c === '"') {
            if (inStr) {
              if (cl !== '"' && cn !== '"') {
                // The next non-blank character is likely the delimiter.
  
                while (cn === ' ') {
                  cn = row[++j];
                }
  
                if (potentialDelimiters[cn]) {
                  delimiterCounts[cn]++;
                  return true;
                }
  
                inStr = false;
              }
            } else {
              inStr = true;
            }
          } else if (potentialDelimiters[c]) {
            if (!isNaN(Date.parse(token))) {
              // Yup, likely the right delimiter
              token = '';
              delimiterCounts[c]++;
            } else if (!isNum(token) && token.length) {
              token = '';
              delimiterCounts[c]++;
            }
          } else {
            token += c;
          }
        }
      });
  
      options.delimiter = ';';
  
      if (
        delimiterCounts[','] > delimiterCounts[';'] &&
        delimiterCounts[','] > delimiterCounts['\t']
      ) {
        options.delimiter = ',';
      }
  
      if (
        delimiterCounts['\t'] >= delimiterCounts[';'] &&
        delimiterCounts['\t'] >= delimiterCounts[',']
      ) {
        options.delimiter = '\t';
      }
    }
  
    rows.forEach(function(row, rowNumber) {
      var cols = [],
        inStr = false,
        i = 0,
        j,
        token = '',
        guessedDel,
        c,
        cp,
        cn;
  
      function pushToken() {
        token = (token || '').replace(/\,/g, '');
        if (!token.length) {
          token = null;
          // return;
        }
  
        if (isNum(token)) {
          token = parseFloat(token);
        }
  
        cols.push(token);
        token = '';
      }
  
      for (i = 0; i < row.length; i++) {
        c = row[i];
        cn = row[i + 1];
        cp = row[i - 1];
  
        if (c === '"') {
          if (inStr) {
            pushToken();
          } else {
            inStr = false;
          }
  
          //Everything is allowed inside quotes
        } else if (inStr) {
          token += c;
          //Check if we're done reading a token
        } else if (c === options.delimiter) {
          pushToken();
  
          //Append to token
        } else {
          token += c;
        }
  
        // Push if this was the last character
        if (i === row.length - 1) {
          pushToken();
        }
      }
  
      result.push(cols);
    });
    return result;
  }
  
