      highed.dom.style(highedChartContainer, {
        /*left: newWidth + 'px',*/
        width: '28%',
        height: '37%'
      });
  
      if (fixedSize) {
        // Update size after the animation is done
        setTimeout(function() {
          sizeChart(fixedSize.w, fixedSize.h);
        }, 400);
        return;
      }
